import React from "react";
import styles from "./App.module.css";

export function TodoBox({ checked, id, value, changeCheckBox }) {
	return (
		<div className={styles.contentBox}>
			{checked === true ? (
				<input
					key={id}
					type="checkbox"
					onChange={() => changeCheckBox(id)}
					checked={checked}
				/>
			) : (
				<input
					key={id}
					type="checkbox"
					onChange={() => changeCheckBox(id)}
					checked={checked}
				/>
			)}
			<div className={styles.todoText}>
			{/* warning 고치기 ) 초기값 설정 */}
				<div>{value !== "" && value}</div> 
			</div>
			<div className={styles.todoIconX}>❌</div>
		</div>
	);
}

export const MemoizedTodoBox = React.memo(TodoBox);
