import { useState, useEffect } from "react";
import TodoBox from "./TodoBox";
import styles from "./App.module.css";

function App() {
	// useState
	const [input, setInput] = useState("");
	const onInputChange = (e) => {
		setInput(e.target.value);
	};
	const makeTodoBox = (input) => {
    const newBox = document.createElement("TodoBox");
    newBox.textContent = 
		document.body.appendChild(<TodoBox value={input} />);
	};
	// useEffect

	return (
		<div className="App">
			<div className={styles.title}>Todo list</div>
			<div
				id="todo-input-wrapper"
				style={{ display: "row" }}>
				<input
					value={input}
					onChange={onInputChange}></input>
				<button>post</button>
			</div>
		</div>
	);
}

export default App;
