import { useState, useEffect } from "react";
import { MemoizedTodoBox } from "./TodoBox"; // memoized 사용함
import styles from "./App.module.css";

function App() {
	// useState
	const [input, setInput] = useState("");
	// for storing(3) : useState의 인자로 넣어주기
	const [todoList, setTodoList] = useState(storeTodoList);

	const changeCheckBox = (id) => {
		// 불변성 원칙에 따라 todoList 업데이트
		setTodoList(
			todoList.map((todo) =>
				todo.id === id ? { ...todo, checked: !todo.checked } : todo
			)
			// todoList.map((todo) =>
			// 	todo.id === id ? console.log("good") : console.log("bad")
			// )
		);
	};

	const onInputChange = (e) => {
		setInput(e.target.value);
	};

	const makeTodoBox = () => {
		if (input.trim() !== "") {
			setTodoList([
				...todoList,
				{ id: todoList.length, content: input, checked: false },
			]);
			setInput("");
		}
	};

	// for storing(1)
	function storeTodoList() {
		// local storage 에 저장
		const todoList = localStorage.getItem("todoList");
		return todoList ? JSON.parse(todoList) : [];
	}

	// for storing(2)
	useEffect(() => {
		localStorage.setItem("todoList", JSON.stringify(todoList));
	}, [todoList]);

	return (
		<div className={styles.app}>
			<div className={styles.title}>Todo list</div>
			<div
				id="todo-input-wrapper"
				style={{ display: "row" }}>
				<input
					value={input}
					onChange={onInputChange}
				/>
				<button onClick={makeTodoBox}>post</button>
			</div>

			{todoList.map((value) => (
				<MemoizedTodoBox
					key={value.id}
					value={value.content}
					checked={value.checked}
					changeCheckBox={changeCheckBox}
				/>
			))}
		</div>
	);
}

export default App;
