import { useState, useEffect } from "react";
import { MemoizedTodoBox } from "./TodoBox"; // memoized 사용함
import styles from "./App.module.css";

function App() {
	// useState
	const [input, setInput] = useState("");
	// for storing(3) : useState의 인자로 넣어주기
	const [todoList, setTodoList] = useState(storeTodoList);

	const [checkbox, setCheckBox] = useState();

	const changeCheckBox = () => {
		setCheckBox((checkbox)=>!checkbox);
	};

	const onInputChange = (e) => {
		setInput(e.target.value);
	};

	const makeTodoBox = () => {
		if (input.trim() !== "") {
			setTodoList([
				...todoList,
				{ id: todoList.length, content: input, checked: false },
			]);
			setInput("");
		}
	};

	// for storing(1)
	function storeTodoList() {
		// local storage 에 저장
		const todoList = localStorage.getItem("todoList");
		return todoList ? JSON.parse(todoList) : [];
	}

	// for storing(2)
	useEffect(() => {
		localStorage.setItem("todoList", JSON.stringify(todoList));
	}, [todoList]);

	console.log(todoList);

	return (
		<div className={styles.app}>
			<div className={styles.title}>Todo list</div>
			<div
				id="todo-input-wrapper"
				style={{ display: "row" }}>
				<input
					value={input}
					onChange={onInputChange}
				/>
				<button onClick={makeTodoBox}>post</button>
			</div> 

			{todoList.map((value) => (
				// console.log("030 ",value)
				<MemoizedTodoBox
					key={value.id}
					value={value.content}
					checked={value.checked}
					style={styles.contentBox}
					input={value}
					// setInput={setInput}
				/>
			))}
		</div>
	);
}

export default App;
