import { useState, useEffect } from "react";
import { MemoizedTodoBox } from "./TodoBox"; // memoized 사용
import styles from "./App.module.css";

function App() {
	// useState
	const [input, setInput] = useState("");
	const [todoList, setTodoList] = useState(storeTodoList);

	const onInputChange = (e) => {
		setInput(e.target.value);
	};

	const makeTodoBox = () => {
		if (input.trim() !== "") {
			setTodoList([
				...todoList,
				{ id: todoList.length, content: input, checked: false }, // 새 항목은 기본적으로 unchecked
			]);
			setInput(""); // 입력 필드 초기화
		}
	};

	const changeCheckBox = (id) => {
		console.log("change check box function executed");
		// console.log(todoList);
		// 불변성 원칙에 따라 todoList 업데이트
		setTodoList(
			todoList.map(
				(todoList) =>
					todoList.id === id
						? { ...todoList, checked: !todoList.checked }
						: todoList // id가 일치하는 항목의 checked 값을 토글
			)
		);
	};

	// for storing(1)
	function storeTodoList() {
		const todoList = localStorage.getItem("todoList");
		return todoList ? JSON.parse(todoList) : [];
	}

	// for storing(2)
	useEffect(() => {
		localStorage.setItem("todoList", JSON.stringify(todoList));
	}, [todoList]);

	return (
		<div className={styles.app}>
			<div className={styles.title}>Todo list</div>
			<div
				id="todo-input-wrapper"
				style={{ display: "row" }}>
				<input
					value={input}
					onChange={onInputChange}
				/>
				<button onClick={makeTodoBox}>post</button>
			</div>

			{todoList.map((todo) => (
				<MemoizedTodoBox
					key={todo.id}
					id={todo.id} // id 전달
					value={todo.content}
					checked={todo.checked} // checked 상태 전달
					changeCheckBox={changeCheckBox} // 함수 전달
				/>
			))}
		</div>
	);
}

export default App;
