import { useState , useEffect } from "react";
import TodoBox from "./TodoBox";

function App() {

  // useState
	const [input, setInput] = useState("");
	const onInputChange = (e) => {
		setInput(e.target.value);
	};

  // useEffect

	return (
		<div className="App">
			<input
				value={input}
				onChange={onInputChange}></input>
		</div>
	);
}

export default App;
