import React from "react";

// MemoizedTodoBox 컴포넌트
const TodoBox = ({ id, value, checked, changeCheckBox }) => {
	return (
		<div>
			<input
				type="checkbox"
				checked={checked} // checked 상태를 전달받음
				onChange={() => changeCheckBox(id)} // id를 전달하여 체크박스 상태 변경
			/>
			<span>{value}</span>
		</div>
	);
};

export const MemoizedTodoBox = React.memo(TodoBox);
