import { useState, useEffect } from "react";
import { MemoizedTodoBox } from "./TodoBox"; // memoized 사용
import styles from "./App.module.css";

function App() {
	// useState
	const [input, setInput] = useState("");
	const [todoList, setTodoList] = useState(storeTodoList);

	const onInputChange = (e) => {
		setInput(e.target.value);
	};

	const makeTodoBox = () => {
		if (input.trim() !== "") {
			setTodoList([
				...todoList,
				{ id: Date.now(), content: input, checked: false }, // 새 항목은 기본적으로 unchecked
			]);
			setInput(""); // 입력 필드 초기화
		}
	};

	const changeCheckBox = (id) => {
		// 불변성 원칙에 따라 todoList 업데이트
		setTodoList(
			todoList.map(
				(todoList) =>
					todoList.id === id
						? { ...todoList, checked: !todoList.checked }
						: todoList // id가 일치하는 항목의 checked 값을 토글
			)
		);
	};

	function deleteTodo(id) {
		// 필터를 사용하여 삭제할 항목을 제외한 새로운 배열을 반환
		return todoList.filter((todo) => todo.id !== id);
	}

	function deleteTodoList(id) {
		// 삭제된 새로운 todoList로 업데이트
		const updatedTodoList = deleteTodo(id);
		setTodoList(updatedTodoList); // 상태 업데이트
	}

	// for storing(1)
	function storeTodoList() {
		const todoList = localStorage.getItem("todoList");
		return todoList ? JSON.parse(todoList) : [];
	}

	function getTodoList(todoList) {
		return localStorage.setItem("todoList", JSON.stringify(todoList));
	}



	// for storing(2)
	useEffect(() => {
		getTodoList(todoList);
		console.log(todoList); //NOTE - for debug
	}, [todoList]);

	return (
		<div className={styles.app}>
			<div className={styles.title}>Todo list</div>
			<div
				id="todo-input-wrapper"
				style={{ display: "row" }}>
				<input
					value={input}
					onChange={onInputChange}
					className={styles.inputWrapper}
					placeholder="Write down your new TODOs! :)"
				/>
				<button
					onClick={makeTodoBox}
					className={styles.postButton}>
					post
				</button>
			</div>

			{todoList.map((todo) => (
				<MemoizedTodoBox
					key={todo.id} // 고유식별번호(꼭 넣어줘야함!!)
					id={todo.id} // id 전달
					value={todo.content}
					checked={todo.checked} // checked 상태 전달
					changeCheckBox={changeCheckBox} // 함수 전달
					deleteTodoList={deleteTodoList} // 함수 전달
				/>
			))}
		</div>
	);
}

export default App;
