import React, { useState } from "react";
import styles from "./App.module.css";
import changeCheckBox from "./App";

export function TodoBox(input) {
	return (
		<div className={styles.contentBox}>
			{input.checked === true ? (
				<input
					id={input.key}
					type="checkbox"
					onClick={changeCheckBox.changeCheckBox()}
					checked
				/>
			) : (
				<input
					id={input.key}
					type="checkbox"
					onClick={functionApp.changeCheckBox()}
				/>
			)}
			<div className={styles.todoText}>
				<div>{input.value}</div>
			</div>
			<div className={styles.todoIconX}>‚ùå</div>
		</div>
	);
}

export const MemoizedTodoBox = React.memo(TodoBox);
