import React from "react";
import styles from "./App.module.css";

export function TodoBox({ checked, id, changeCheckBox }) {
	return (
		<div className={styles.contentBox}>
			{checked === true ? (
				<input
					key={id}
					type="checkbox"
					onChange={() => changeCheckBox(id)}
					checked
				/>
			) : (
				<input
					key={id}
					value={value}
					type="checkbox"
					onChange={() => changeCheckBox(id)}
				/>
			)}
			<div className={styles.todoText}>
				<div>{value}</div>
			</div>
			<div className={styles.todoIconX}>‚ùå</div>
		</div>
	);
}

