import React from "react";
import styles from "./App.module.css";

export function TodoBox({ checked, key, value, changeCheckBox }) {
	return (
		<div className={styles.contentBox}>
			{checked === true ? (
				<input
					key={key}
					type="checkbox"
					onChange={() => changeCheckBox(key)}
					checked={checked}
				/>
			) : (
				<input
					key={key}
					type="checkbox"
					onChange={() => changeCheckBox(key)}
					checked={checked}
				/>
			)}
			<div className={styles.todoText}>
			{/* warning 고치기 ) 초기값 설정 */}
				<div>{value !== "" && value}</div> 
			</div>
			<div className={styles.todoIconX}>❌</div>
		</div>
	);
}

